import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state={
    counter:0
  }

  renderButtonComponent(text,segno){
    return(
      <button onClick={(segno)=> this.setState({counter: this.state.counter +1})}>
        {text}
      </button>
    )
  }

  render() {
    console.log(this.props.pippo);
    return (
      <div>
        <h1>Counter: {this.state.counter}</h1>
        renderButtonComponent("Increment","+");

        <button onClick={()=> this.setState({counter: this.state.counter -1})}>
          Decrement
        </button>
      </div>
    );

  }
}

export default App;
