import React, { Component } from 'react';

import './App.css';



class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      currentTodo: '',
      todos: []
    };
  }

  aggiorna(event){
    this.setState({currentTodo:event.target.value})
  }

  render() {

    return (
        <div className="App">
            <form>
              <input value={this.state.currentTodo} onChange={this.aggiorna}/>
              <button>VAI!</button>
            </form>
        </div>

    );
  }
}



export default App;
