import React, { Component } from 'react';

import './App.css';



class App extends Component {

  constructor(props) {

    super(props);

    this.state = {

      searchValue: '',

      img: ''

    };

  }



  onChange = (event) => {

    this.setState({ searchValue: event.target.value });

  }





    handleSubmit = (event) => {

      event.preventDefault();
            mm.Player.tone.context.resume();  // enable audio
            model.sample(1)
              .then((samples) => player.start(samples[0], 80));
          }


  render() {
    const url = 'https://storage.googleapis.com/magentadata/js/checkpoints/music_vae/trio_4bar';
    const model = new mm.MusicVAE(url);
    const player = new mm.Player();
    return (

      <div className="App">


        <form onSubmit={this.handleSubmit}>

          <input value={this.state.searchValue} onChange={this.onChange} />

          <button>Search!</button>

        </form>

        <img src={this.state.img} height="200" alt={this.state.searchValue} />

      </div>

    );

  }

}



export default App;
