import React, { Component } from 'react';

import './App.css';



class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      currentTodo: '',
      todos: []
    };

    this.onPress = this.onPress.bind(this);
  }

  aggiorna(event){
    this.setState({currentTodo:event.target.value})
  }

  onPress(){
    //event.preventDefault()
    this.setState({
      currentTodo: '', //We clean the input text
      todos: [...this.state.todos, this.state.currentTodo]//aggiunge il todo nuovo
    });

  }

  render() {

    return (
        <div className="App">
            <form >
              <input value={this.state.currentTodo} onChange={this.aggiorna}/>
              <button onClick={this.onPress}>Aggiorna!</button>
            </form>
        </div>

    );
  }
}



export default App;
